// src/components/PaymentPage.js
import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import Header from './Header';
import Footer from './Footer';
import styles from '../css/PaymentPage.module.css';

const stripePromise = loadStripe(
  process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY
);

function CheckoutForm({ clientSecret }) {
  const stripe = useStripe();
  const elements = useElements();
  const [errorMessage, setErrorMessage] = useState('');
  const [processing, setProcessing] = useState(false);

  const handleChange = e => setErrorMessage(e.error?.message || '');

  const handleSubmit = async e => {
    e.preventDefault();
    if (!stripe || !elements) return;
    setProcessing(true);
    const { error, paymentIntent } = await stripe.confirmCardPayment(
      clientSecret,
      { payment_method: { card: elements.getElement(CardElement) } }
    );
    if (error) {
      setErrorMessage(error.message);
      setProcessing(false);
    } else if (paymentIntent.status === 'succeeded') {
      window.location.reload();
    }
  };

  return (
    <form onSubmit={handleSubmit} className={styles.form}>
      <div className={styles.cardContainer}>
        <CardElement
          options={{ hidePostalCode: true }}
          onChange={handleChange}
        />
      </div>
      {errorMessage && <p className={styles.cardError}>{errorMessage}</p>}
      <button
        type="submit"
        disabled={!stripe || processing}
        className={styles.payButton}
      >
        {processing ? 'Processing…' : 'Pay Now'}
      </button>
    </form>
  );
}

export default function PaymentPage() {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const reference = searchParams.get('reference');
  const [clientSecret, setClientSecret] = useState('');

  useEffect(() => {
    if (!reference) return;
    fetch('/api/create-payment-intent', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ reference })
    })
      .then(r => r.json())
      .then(data => {
        if (data.error) throw new Error(data.error);
        setClientSecret(data.clientSecret);
      })
      .catch(e => setClientSecret('ERROR:' + e.message));
  }, [reference]);

  return (
    <>
      <Header title="Complete Your Payment" />
      <main className={styles.wrapper}>
        <button onClick={() => navigate(-1)} className={styles.backButton}>
          ← Back to Booking
        </button>

        {!reference && (
          <p className={styles.errorText}>Missing reservation reference.</p>
        )}

        {clientSecret.startsWith('ERROR:') ? (
          <p className={styles.errorText}>{clientSecret.slice(6)}</p>
        ) : clientSecret ? (
          <Elements stripe={stripePromise}>
            <CheckoutForm clientSecret={clientSecret} />
          </Elements>
        ) : (
          <p>Loading payment form…</p>
        )}
      </main>
      <Footer />
    </>
  );
}
