import React from 'react';

export default function BookingFormFields({ form, onChange, dates, times, selectedDate, selectedTime, onDateChange, onTimeChange }) {
  return (
    <div style={{ padding: '1rem' }}>
      {['firstName','surname','phone','email','address','postcode'].map(name => (
        <div key={name} style={{ margin: '0.5rem 0' }}>
          <label>
            {name.charAt(0).toUpperCase() + name.slice(1)}:
            <input
              type={name === 'email' ? 'email' : 'text'}
              name={name}
              value={form[name]}
              onChange={onChange}
              style={{ marginLeft: '0.5rem' }}
            />
          </label>
        </div>
      ))}

      {/* Date dropdown */}
      {dates.length > 0 && (
        <div style={{ marginTop: '1rem' }}>
          <label>
            Date:{' '}
            <select value={selectedDate} onChange={onDateChange}>
              <option value="">--Select Date--</option>
              {dates.map(d => <option key={d} value={d}>{d}</option>)}
            </select>
          </label>
        </div>
      )}

      {/* Time dropdown */}
      {times.length > 0 && (
        <div style={{ marginTop: '1rem' }}>
          <label>
            Time:{' '}
            <select value={selectedTime} onChange={onTimeChange}>
              <option value="">--Select Time--</option>
              {times.map(t => <option key={t} value={t}>{t}</option>)}
            </select>
          </label>
        </div>
      )}
    </div>
  );
}
