import React, { useState, useEffect } from 'react';

function formatDate(date) {
  return date.toISOString().split('T')[0];
}

export default function BookingForm() {
  const params = new URLSearchParams(window.location.search);
  const today = new Date();
  const sevenDays = new Date(today);
  sevenDays.setDate(today.getDate() + 7);

  // Form fields with UTM defaults
  const [form, setForm] = useState({
    firstName: params.get('utm_firstName') || '',
    surname:   params.get('utm_surname')   || '',
    phone:     params.get('utm_phone')     || '',
    email:     params.get('utm_email')     || '',
    address:   params.get('utm_address')   || '',
    postcode:  params.get('utm_postcode')  || ''
  });

  const [slots, setSlots] = useState([]);
  const [dates, setDates] = useState([]);
  const [times, setTimes] = useState([]);
  const [selectedDate, setSelectedDate] = useState('');
  const [selectedTime, setSelectedTime] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = e => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const fetchSlots = async () => {
    setLoading(true);
    setError(null);
    const payload = {
      clientId: `${form.firstName}_${form.surname}`,
      clientRef: form.email,
      visitType: 'Service',
      postcode: form.postcode,
      earliestDate: formatDate(today),
      latestDate: formatDate(sevenDays)
    };
    try {
      const res = await fetch(
        (process.env.REACT_APP_API_BASE_URL || 'http://localhost:3001') + '/api/available-slots',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(payload)
        }
      );
      if (!res.ok) throw new Error(`HTTP ${res.status}`);
      const { slots: fetched = [] } = await res.json();
      setSlots(fetched);
      setDates(Array.from(new Set(fetched.map(s => s.visitDate))));
      setSelectedDate('');
      setTimes([]);
      setSelectedTime('');
    } catch (e) {
      setError(e.message);
      setSlots([]);
      setDates([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (selectedDate) {
      setTimes(
        slots
          .filter(s => s.visitDate === selectedDate)
          .map(s => `${s.windowStart}–${s.windowEnd}`)
      );
      setSelectedTime('');
    }
  }, [selectedDate, slots]);

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Booking Form</h2>

      {/* Personal Details */}
      <div style={{ marginBottom: '1rem' }}>
        {['firstName','surname','phone','email','address','postcode'].map(name => (
          <div key={name} style={{ margin: '0.5rem 0' }}>
            <label>
              {name.charAt(0).toUpperCase() + name.slice(1)}:
              <input
                type={name === 'email' ? 'email' : 'text'}
                name={name}
                value={form[name]}
                onChange={handleChange}
                style={{ marginLeft: '0.5rem' }}
              />
            </label>
          </div>
        ))}
      </div>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <button
        onClick={fetchSlots}
        disabled={loading}
        style={{ padding: '0.5rem 1rem' }}
      >
        {loading ? 'Loading...' : 'Get Available Dates'}
      </button>

      {/* Date dropdown */}
      {dates.length > 0 && (
        <div style={{ marginTop: '1rem' }}>
          <label>
            Date:{' '}
            <select
              value={selectedDate}
              onChange={e => setSelectedDate(e.target.value)}
            >
              <option value="">--Select Date--</option>
              {dates.map(d => <option key={d} value={d}>{d}</option>)}
            </select>
          </label>
        </div>
      )}

      {/* Time dropdown */}
      {times.length > 0 && (
        <div style={{ marginTop: '1rem' }}>
          <label>
            Time:{' '}
            <select
              value={selectedTime}
              onChange={e => setSelectedTime(e.target.value)}
            >
              <option value="">--Select Time--</option>
              {times.map(t => <option key={t} value={t}>{t}</option>)}
            </select>
          </label>
        </div>
      )}

      {/* Confirmation */}
      {selectedDate && selectedTime && (
        <p style={{ marginTop: '1rem' }}>
          You selected: {selectedDate}, {selectedTime}
        </p>
      )}
    </div>
  );
}
