import React, { useState } from 'react';

function formatDate(date) {
  return date.toISOString().split('T')[0];
}

export default function BookingForm() {
  const [slots, setSlots] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [dates, setDates] = useState([]);
  const [times, setTimes] = useState([]);
  const [selectedDate, setSelectedDate] = useState('');
  const [selectedTime, setSelectedTime] = useState('');

  const handleClick = async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await fetch(
        `${process.env.REACT_APP_API_BASE_URL}/api/available-slots`,
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            clientId: 'MyHomeSmart_tgb56du',
            clientRef: 'EXAMPLE12345',
            visitType: 'Service',
            postcode: 'EH45 9DB',
            earliestDate: formatDate(new Date()),
            latestDate: formatDate(new Date(Date.now() + 10 * 86400000))
          })
        }
      );
      if (!res.ok) throw new Error(`HTTP ${res.status}`);
      const data = await res.json();
      const fetchedSlots = data.slots || [];
      // Extract unique dates
      const uniqueDates = Array.from(new Set(fetchedSlots.map(s => s.visitDate)));
      setDates(uniqueDates);
      setSlots(fetchedSlots);
      setSelectedDate('');
      setTimes([]);
      setSelectedTime('');
    } catch (e) {
      setError(e.message);
      setSlots([]);
      setDates([]);
    } finally {
      setLoading(false);
    }
  };

  const handleDateChange = e => {
    const date = e.target.value;
    setSelectedDate(date);
    const availableTimes = slots
      .filter(s => s.visitDate === date)
      .map(s => `${s.windowStart}â€“${s.windowEnd}`);
    setTimes(availableTimes);
    setSelectedTime('');
  };

  return (
    <div>
      <h2>Booking Form</h2>
      <button onClick={handleClick} disabled={loading}>
        {loading ? 'Loading...' : 'Get Available Dates'}
      </button>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {dates.length > 0 && (
        <div style={{ marginTop: '1rem' }}>
          <label>
            Select Date:{' '}
            <select value={selectedDate} onChange={handleDateChange}>
              <option value="">--Choose a date--</option>
              {dates.map((d, i) => (
                <option key={i} value={d}>{d}</option>
              ))}
            </select>
          </label>
        </div>
      )}

      {times.length > 0 && (
        <div style={{ marginTop: '1rem' }}>
          <label>
            Select Time:{' '}
            <select value={selectedTime} onChange={e => setSelectedTime(e.target.value)}>
              <option value="">--Choose a time--</option>
              {times.map((t, i) => (
                <option key={i} value={t}>{t}</option>
              ))}
            </select>
          </label>
        </div>
      )}

      {selectedDate && selectedTime && (
        <p style={{ marginTop: '1rem' }}>
          You selected: {selectedDate}, {selectedTime}
        </p>
      )}

      {slots.length === 0 && !loading && dates.length === 0 && (
        <p style={{ marginTop: '1rem' }}>No slots available.</p>
      )}
    </div>
  );
}
