import React from 'react';
import { useSearchParams } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';

export default function PaymentPage() {
  const [searchParams] = useSearchParams();
  const reference = searchParams.get('reference');

  return (
    <>
      <Header title="Complete Your Payment" />

      <div style={{ maxWidth: '600px', margin: '2rem auto', padding: '1rem' }}>
        {reference ? (
          <>
            <h2>Reservation #{reference}</h2>
            <p>Please enter your payment details below to confirm your booking.</p>
            {/* TODO: insert your payment form or redirect to payment gateway here */}
          </>
        ) : (
          <p style={{ color: 'red' }}>
            No reservation reference found. Please complete your booking first.
          </p>
        )}
      </div>

      <Footer />
    </>
  );
}
