import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import {
  Elements,
  CardElement,
  useStripe,
  useElements
} from '@stripe/react-stripe-js';
import Header from './Header';
import Footer from './Footer';

const stripePromise = loadStripe(
  process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY
);

function CheckoutForm({ clientSecret, reference }) {
  const stripe = useStripe();
  const elements = useElements();
  const [status, setStatus] = useState('entering'); // 'entering' | 'success' | 'error'
  const [message, setMessage] = useState('');

  const handleSubmit = async e => {
    e.preventDefault();
    if (!stripe || !elements) return;

    const { error, paymentIntent } = await stripe.confirmCardPayment(
      clientSecret,
      {
        payment_method: {
          card: elements.getElement(CardElement),
          billing_details: { name: reference }
        }
      }
    );

    if (error) {
      setStatus('error');
      setMessage(error.message);
    } else if (paymentIntent.status === 'succeeded') {
      setStatus('success');
      setMessage(
        `Thank you! Your payment succeeded. Ref: ${reference}. 
         You can now check your email for details or call us at XXXXX.`
      );
    }
  };

  if (status === 'success' || status === 'error') {
    return <div style={{ marginTop: '2rem', color: status === 'error' ? 'red' : 'green' }}>
      {message}
    </div>;
  }

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 400 }}>
      <CardElement options={{ hidePostalCode: true }} />
      <button
        type="submit"
        disabled={!stripe}
        style={{
          marginTop: '1rem',
          padding: '0.75rem',
          background: '#28a745',
          color: '#fff',
          border: 'none',
          borderRadius: 4,
          cursor: 'pointer'
        }}
      >
        Pay £{(1000/100).toFixed(2)}
      </button>
    </form>
  );
}

export default function PaymentPage() {
  const [searchParams] = useSearchParams();
  const reference = searchParams.get('reference');
  const [clientSecret, setClientSecret] = useState('');

  useEffect(() => {
    // create a PaymentIntent as soon as we mount
    fetch('/api/create-payment-intent', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ reference, amount: 1000 })
    })
      .then(r => r.json())
      .then(data => {
        if (data.error) throw new Error(data.error);
        setClientSecret(data.clientSecret);
      })
      .catch(e => setClientSecret('ERROR:' + e.message));
  }, [reference]);

  return (
    <>
      <Header title="Complete Your Payment" />
      <main style={{ padding: '2rem', textAlign: 'center' }}>
        {!reference && <p style={{ color: 'red' }}>Missing reference!</p>}
        {clientSecret.startsWith('ERROR:') && (
          <p style={{ color: 'red' }}>{clientSecret.slice(6)}</p>
        )}
        {clientSecret && !clientSecret.startsWith('ERROR:') ? (
          <Elements stripe={stripePromise}>
            <CheckoutForm
              clientSecret={clientSecret}
              reference={reference}
            />
          </Elements>
        ) : (
          <p>Loading payment form…</p>
        )}
      </main>
      <Footer />
    </>
  );
}
