import React, { useState } from 'react';

function formatDate(date) {
  return date.toISOString().split('T')[0];
}

export default function BookingForm() {
  const [slots, setSlots] = useState([]);
  const [rawResponse, setRawResponse] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleClick = async () => {
    setLoading(true);
    setError(null);
    setRawResponse(null);
    try {
      const res = await fetch(
        `${process.env.REACT_APP_API_BASE_URL}/api/available-slots`,
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            clientId: 'MyHomeSmart_tgb56du',
            clientRef: 'EXAMPLE12345',
            visitType: 'Service',
            postcode: 'EH45 9DB',
            earliestDate: formatDate(new Date()),
            latestDate: formatDate(new Date(Date.now() + 10 * 86400000))
          })
        }
      );
      if (!res.ok) throw new Error(`HTTP ${res.status}`);
      const data = await res.json();
      setSlots(data.slots || []);
      setRawResponse(data.rawResponse || null);
    } catch (e) {
      setError(e.message);
      setSlots([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Booking Form</h2>
      <button onClick={handleClick} disabled={loading}>
        {loading ? 'Loading...' : 'Get Available Dates'}
      </button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {slots.length > 0 ? (
        <table style={{ marginTop: '1rem', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th style={{ border: '1px solid #ccc', padding: '0.5rem' }}>Date</th>
              <th style={{ border: '1px solid #ccc', padding: '0.5rem' }}>Window</th>
              <th style={{ border: '1px solid #ccc', padding: '0.5rem' }}>Availability</th>
              <th style={{ border: '1px solid #ccc', padding: '0.5rem' }}>Engineers</th>
            </tr>
          </thead>
          <tbody>
            {slots.map((s, i) => (
              <tr key={i}>
                <td style={{ border: '1px solid #ccc', padding: '0.5rem' }}>{s.visitDate}</td>
                <td style={{ border: '1px solid #ccc', padding: '0.5rem' }}>{s.windowStart}–{s.windowEnd}</td>
                <td style={{ border: '1px solid #ccc', padding: '0.5rem' }}>{s.availability}</td>
                <td style={{ border: '1px solid #ccc', padding: '0.5rem' }}>{s.engineers}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        !loading && (
          rawResponse ? (
            <div style={{ marginTop: '1rem' }}>
              <h3>Raw Response (XML)</h3>
              <pre style={{ whiteSpace: 'pre-wrap', background: '#f9f9f9', padding: '0.5rem' }}>
                {rawResponse}
              </pre>
            </div>
          ) : (
            <p style={{ marginTop: '1rem' }}>No slots available.</p>
          )
        )
      )}
    </div>
  );
}
