// src/components/PaymentPage.js
import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import {
  Elements,
  CardNumberElement,
  CardExpiryElement,
  CardCvcElement,
  useStripe,
  useElements
} from '@stripe/react-stripe-js';
import Header from './Header';
import Footer from './Footer';
import styles from '../css/PaymentPage.module.css';

const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY);

function CheckoutForm({ clientSecret, productName, unitAmount, currency }) {
  const stripe = useStripe();
  const elements = useElements();
  const [errorMessage, setErrorMessage] = useState('');
  const [processing, setProcessing] = useState(false);

  const handleChange = e => setErrorMessage(e.error?.message || '');

  const handleSubmit = async e => {
    e.preventDefault();
    if (!stripe || !elements) return;
    setProcessing(true);
    setErrorMessage('');

    const cardNumber = elements.getElement(CardNumberElement);
    const { error, paymentIntent } = await stripe.confirmCardPayment(
      clientSecret,
      { payment_method: { card: cardNumber } }
    );

    if (error) {
      setErrorMessage(error.message);
      setProcessing(false);
    } else if (paymentIntent.status === 'succeeded') {
      window.location.reload();
    }
  };

  return (
    <form onSubmit={handleSubmit} className={styles.form}>
      <div className={styles.fieldGroup}>
        <label className={styles.label}>Card Number</label>
        <div className={styles.cardInput}>
          <CardNumberElement
            options={{ style: { base: { fontSize: '16px' } } }}
            onChange={handleChange}
          />
        </div>
      </div>

      <div className={styles.fieldGroupRow}>
        <div className={styles.fieldGroupSmall}>
          <label className={styles.label}>Expiry</label>
          <div className={styles.cardInput}>
            <CardExpiryElement
              options={{ style: { base: { fontSize: '16px' } } }}
              onChange={handleChange}
            />
          </div>
        </div>
        <div className={styles.fieldGroupSmall}>
          <label className={styles.label}>CVC</label>
          <div className={styles.cardInput}>
            <CardCvcElement
              options={{ style: { base: { fontSize: '16px' } } }}
              onChange={handleChange}
            />
          </div>
        </div>
      </div>

      {errorMessage && <p className={styles.cardError}>{errorMessage}</p>}

      <button
        type="submit"
        disabled={!stripe || processing}
        className={styles.payButton}
      >
        {processing
          ? 'Processing…'
          : `Pay £${(unitAmount / 100).toFixed(2)}`}
      </button>
    </form>
  );
}

export default function PaymentPage() {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const reference = searchParams.get('reference');
  const [clientSecret, setClientSecret] = useState('');
  const [productName, setProductName]   = useState('');
  const [unitAmount, setUnitAmount]     = useState(0);
  const [currency, setCurrency]         = useState('');

  useEffect(() => {
    if (!reference) return;
    fetch('/api/create-payment-intent', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ reference })
    })
      .then(r => r.json())
      .then(data => {
        if (data.error) throw new Error(data.error);
        setClientSecret(data.clientSecret);
        setProductName(data.productName);
        setUnitAmount(data.unitAmount);
        setCurrency(data.currency);
      })
      .catch(e => console.error(e));
  }, [reference]);

  return (
    <>
      <Header title="Complete Your Payment" />
      <main className={styles.wrapper}>
        <button onClick={() => navigate(-1)} className={styles.backButton}>
          ← Back to Booking
        </button>

        {!reference && (
          <p className={styles.errorText}>Missing reservation reference.</p>
        )}

        {clientSecret ? (
          <>
            <h2 className={styles.productTitle}>{productName}</h2>
            <p className={styles.productPrice}>
              £{(unitAmount / 100).toFixed(2)} {currency.toUpperCase()}
            </p>

            <Elements stripe={stripePromise}>
              <CheckoutForm
                clientSecret={clientSecret}
                productName={productName}
                unitAmount={unitAmount}
                currency={currency}
              />
            </Elements>
          </>
        ) : (
          <p>Loading payment details…</p>
        )}
      </main>
      <Footer />
    </>
  );
}
