import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import {
  Elements,
  CardElement,
  useStripe,
  useElements

} from '@stripe/react-stripe-js';
import Header from './Header';
import Footer from './Footer';
import styles from '../css/PaymentPage.module.css';

const stripePromise = loadStripe(
  process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY
);

function CheckoutForm({ clientSecret, reference }) {
  const stripe = useStripe();
  const elements = useElements();
  const [errorMessage, setErrorMessage] = useState('');
  const [processing, setProcessing] = useState(false);

  // handle real-time validation errors
  const handleChange = (event) => {
    setErrorMessage(event.error ? event.error.message : '');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!stripe || !elements) return;
    setProcessing(true);
    setErrorMessage('');

    const card = elements.getElement(CardElement);
    const { error, paymentIntent } = await stripe.confirmCardPayment(
      clientSecret,
      {
        payment_method: { card, billing_details: { name: reference } }
      }
    );

    if (error) {
      setErrorMessage(error.message);
      setProcessing(false);
    } else if (paymentIntent.status === 'succeeded') {
      // You could also redirect or reload here
      window.location.reload();
    }
  };

  return (
    <form onSubmit={handleSubmit} className={styles.form}>
      <div className={styles.cardContainer}>
        <CardElement
          options={{
             hidePostalCode: true,   // ← this removes the postal-code box
            style: {
              base: { fontSize: '16px', color: '#424770' },
              invalid: { color: '#9e2146' }
            }
          }}
          onChange={handleChange}
        />
      </div>
      {errorMessage && (
        <p className={styles.cardError}>{errorMessage}</p>
      )}
      <button
        type="submit"
        disabled={!stripe || processing}
        className={styles.payButton}
      >
        {processing ? 'Processing…' : 'Pay £10.00'}
      </button>
    </form>
  );
}

export default function PaymentPage() {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const reference = searchParams.get('reference');
  const [clientSecret, setClientSecret] = useState('');

  useEffect(() => {
  if (!reference) return;
  fetch('/api/create-payment-intent', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ reference, amount: 1000 })
    })
      .then(r => r.json())
      .then(data => {
        if (data.error) throw new Error(data.error);
        setClientSecret(data.clientSecret);
      })
      .catch(e => setClientSecret('ERROR:' + e.message));
  }, [reference]);

  return (
    <>
      <Header title="Complete Your Payment" />

      <main className={styles.wrapper}>
        <button
          type="button"
          onClick={() => navigate(-1)}
          className={styles.backButton}
        >
          ← Back to Booking
        </button>

        {!reference && (
          <p className={styles.errorText}>
            Missing reservation reference.
          </p>
        )}

        {clientSecret.startsWith('ERROR:') && (
          <p className={styles.errorText}>
            {clientSecret.slice(6)}
          </p>
        )}

        {clientSecret && !clientSecret.startsWith('ERROR:') ? (
          <Elements stripe={stripePromise}>
            <CheckoutForm
              clientSecret={clientSecret}
              reference={reference}
            />
          </Elements>
        ) : (
          <p>Loading payment form…</p>
        )}
      </main>

      <Footer />
    </>
  );
}
